      "colors": { "outer": "teal", "inner": "white", "highlight_segment": "orange" },
      "annotations": [
        "sector labels around ring: Entertainment, Mobility, Healthcare, Work, Manufacturing",
        "center user icon"
      ]
    },
    {
      "id": "visual_middle_ring",
      "type": "diagram",
      "diagram_type": "concentric_ring",
      "represents": "L2 (Single) Market Level",
      "position": { "bbox_norm": { "x": 0.06, "y": 0.36, "w": 0.32, "h": 0.22 } },
