{
  "artifact_type": "assessment_template",
  "title": "Platform Architecture Assessment",
  "version": "v1",
  "description": "One-page scoring template to evaluate platform architecture across five pillars with guidance questions, recommended operational practices, and scorer input areas.",
  "layout": {
    "columns": [
      "criteria_left",
